package pe.edu.utp.outimportec.utils;

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.charset.StandardCharsets;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;

import net.sf.jasperreports.engine.*;
import net.sf.jasperreports.engine.design.JasperDesign;
import net.sf.jasperreports.engine.xml.JRXmlLoader;
import org.springframework.core.io.ClassPathResource;

import com.fasterxml.jackson.core.type.TypeReference;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.google.gson.GsonBuilder;

import lombok.extern.log4j.Log4j2;
import net.sf.jasperreports.engine.data.JsonDataSource;
import pe.edu.utp.outimportec.errors.DocumentException;
import pe.edu.utp.outimportec.model.Carrito;


@Log4j2
public final class FileUtil {

    private FileUtil() {
        throw new IllegalStateException("Utility class");
    }

    public static byte[] generarReporte(byte[] items, Map<String, Object> parameters) {
        String template = "reporte/boleta_pago_importec.jrxml";
        String logo = "";
        parameters.put("LOGO", logo);
        try {
            ByteArrayInputStream jsonDataStream = new ByteArrayInputStream(items);

            JRDataSource jsonDataSource = new JsonDataSource(jsonDataStream);
            JasperPrint jasperPrint = JasperFillManager.fillReport(loadTemplate(template), parameters, jsonDataSource);
            // JasperExportManager.exportReportToPdfFile(jasperPrint, "boleta_pago_importec.pdf");
            return JasperExportManager.exportReportToPdf(jasperPrint);
        } catch (Exception e) {
            log.error("Ocurrio un error al generar el reporte = {} | error = {}", template, e);
            e.printStackTrace();
            return new byte[0];
        }
    }

    private static JasperReport loadTemplate(String template) throws JRException {
        log.info(String.format("Compile template path : %s", template));
        try (final InputStream reportInputStream = new ClassPathResource(template).getInputStream()) {
            JasperDesign jasperDesign = JRXmlLoader.load(reportInputStream);
            return JasperCompileManager.compileReport(jasperDesign);
        } catch (IOException e) {
            log.error("Ocurrio un error al cargar la plantilla = {}", template);
            throw new DocumentException(e.getMessage());
        }
    }

    public static byte[] StringFromJson(List<Carrito> items) {
        List<Map<String, String>> list = items.stream().map(item -> {
            Map<String, String> map = new HashMap<>();
            map.put("precio", item.getPrecio().toString());
            map.put("cantidad", item.getCantidad().toString());
            map.put("subtotal",item.getSubtotal().toString());
            map.put("descripcion", item.getProducto().getNombre() + "\n - " + item.getProducto().getDescripcion());
            return map;
        }).collect(Collectors.toList());
        GsonBuilder gson = new GsonBuilder().setPrettyPrinting();
        return gson.create().toJson(list).getBytes(StandardCharsets.UTF_8);
    }
}
